//Given two strings s1 and s2, return true if s2 contains a permutation of s1, or false otherwise.

//In other words, return true if one of s1's permutations is the substring of s2.



//Example 1:

//Input: s1 = "ab", s2 = "eidbaooo"
//Output: true
//Explanation: s2 contains one permutation of s1 ("ba").

//Example 2:

//Input: s1 = "ab", s2 = "eidboaoo"
//Output: false

bool CheckInclusion(string s1, string s2)
{
    Dictionary<char,int> s1Char = new Dictionary<char,int>();
    Dictionary<char,int> comparer = new Dictionary<char,int>();
    int count = 0;
    foreach(char c in s1)
    {
        if(s1Char.ContainsKey(c))
            s1Char[c]++;
        else
        {
            s1Char.Add(c, 1);
            comparer.Add(c, 0);
        }
    }

    int left = 0;
    int right = 0;
    var temp = new Dictionary<char,int>(s1Char);
    while(right < s2.Length)
    {
        if((right - left+1) <=s1.Length && temp.ContainsKey(s2[right]))
        {
            temp[s2[right]]--;
            right++;
            if(temp.SequenceEqual(comparer)) return true;
        }
        else
        {
            left++;
            right = left;
            temp = new Dictionary<char,int>(s1Char);
        }
    }
    return false;

}
string s1 = "";
string s2 = "";
if(CheckInclusion(s1, s2))
Console.WriteLine("True");
else
{
    Console.WriteLine("false");
}